  const percentage = (points / maxPossiblePoints) * 100;

  let emoji;
  if (percentage === 100) emoji = "üéñÔ∏è";
  if (percentage)
    return (
      <p className="result">
        you scored<strong>{points}</strong>out of {maxPossiblePoints}(
        {Math.ceil(percentage)}%)
      </p>
    );
}

export default FinishScreen;
